```cpp
#include <iostream>

int main() {
    double num1, num2;
    char operation;

    std::cout << "Enter first number: ";
    std::cin >> num1;

    std::cout << "Enter second number: ";
    std::cin >> num2;

    std::cout << "Choose an operator (+, -, *, /): ";
    std::cin >> operation;

    switch (operation) {
        case '+':
            double sum = num1 + num2;
            std::cout << "Sum: " << sum << std::endl;
            break;

        case '-':
            double difference = num1 - num2;
            std::cout << "Difference (num1-num2): " << difference << std::endl;
            break;

        case '*':
            double product = num1 * num2;
            std::cout << "Product: " << product << std::endl;
            break;

        case '/':
            if(num2 == 0) { // Check for division by zero error.
                std::cerr << "Error! Division by zero is not allowed." << std::endl;
                return EXIT_FAILURE;
            } else {
                double quotient = num1 / num2;
                std::cout << "Quotient (num1/num2): " << quotient << std::endl;
            }
            break;

        default: // Handle invalid operator input.
            std::cerr << "Error! Invalid operator." << std::endl;
            return EXIT_FAILURE;
    }

    return 0;
}
```
